function c(){return{code:"en-US",currency:"USD"}}function m(r){const{code:n,currency:t}=c();if(!r)return"";const e=Number(r);return new Intl.NumberFormat(n,{style:"currency",currency:t,minimumFractionDigits:0,maximumFractionDigits:2}).format(e)}function u(r){const{code:n}=c();if(!r)return"";const t=Number(r);return new Intl.NumberFormat(n,{notation:"compact",maximumFractionDigits:2}).format(t).replace(/[A-Z]/g,o=>o.toLowerCase())}export{m as a,u as f};
